mapper.insert:
 INSERT INTO MP_MEMBER(   USERID 
					       , USERPASS 
					       , USERNAME 	)
               VALUES(    #{userId} 
              		   , #{userPass}
              		   , #{userName})

mapper.select:
SELECT USERID, USERPASS, USERNAME
  FROM MP_MEMBER
 WHERE USERID = #{userId}

mapper.update:
UPDATE MP_MEMBER SET 

USERNAME = #{userName}
WHERE USERID = #{userId}

mapper.select:
SELECT COUNT(*) FROM MP_MEMBER
WHERE USERID = #{userId}
  AND USERPASS = #{userPass}


		SELECT rn, bno, teg, title, writer, regdate FROM
